#[test]
fn test_get_compiler_version() {
    let client = TestClient::new();

    let state_init = TestClient::tvc("t24_initdata", Some(2)).unwrap();

    let code = client
        .request::<_, ResultOfDecodeStateInit>(
            "boc.decode_state_init",
            ParamsOfDecodeStateInit {
                state_init,
                boc_cache: None,
            },
        )
        .unwrap()
        .code
        .unwrap();

    let result: ResultOfGetCompilerVersion = client
        .request(
            "boc.get_compiler_version",
            ParamsOfGetCompilerVersion { code },
        )
        .unwrap();

    assert_eq!(result.version.as_deref(), Some("sol 0.51.0"));
}

#[test]
fn encode_external_in_message() {
    TestClient::init_log();
    let client = TestClient::new_with_config(serde_json::json!({}));
    let (events_abi, events_tvc) = TestClient::package(EVENTS_OLD, Some(2));
    let keys = KeyPair {
        public: "4c7c408ff1ddebb8d6405ee979c716a14fdd6cc08124107a61d3c25597099499".into(),
        secret: "cc8929d635719612a9478b9cd17675a39cfad52d8959e8a177389b8c0b9122a7".into(),
    };
    let abi = events_abi.clone();
    let time: u64 = 1599458364291;
    let expire: u32 = 1599458404;

    let deploy_params = |signing: Signer| ParamsOfEncodeMessage {
        abi: abi.clone(),
        deploy_set: Some(DeploySet {
            tvc: events_tvc.clone(),
            ..Default::default()
        }),
        call_set: Some(CallSet {
            function_name: "constructor".into(),
            header: Some(FunctionHeader {
                pubkey: Some(keys.public.clone()),
                time: Some(time),
                expire: Some(expire),
            }),
            input: None,
        }),
        signer: signing,
        ..Default::default()
    };

    let abi_encoded: ResultOfEncodeMessage = client
        .request(
            "abi.encode_message",
            deploy_params(Signer::Keys { keys: keys.clone() }),
        )
        .unwrap();
    assert_eq!(abi_encoded.message, "");

    let abi_parsed = client
        .request::<ParamsOfParse, ResultOfParse>(
            "boc.parse_message",
            ParamsOfParse {
                boc: abi_encoded.message.clone(),
            },
        )
        .unwrap()
        .parsed;
    let init = client
        .request::<ParamsOfEncodeStateInit, ResultOfEncodeStateInit>(
            "boc.encode_state_init",
            ParamsOfEncodeStateInit {
                code: abi_parsed["code"].as_str().map(|x| x.to_string()),
                data: abi_parsed["data"].as_str().map(|x| x.to_string()),
                library: abi_parsed["library"].as_str().map(|x| x.to_string()),
                ..Default::default()
            },
        )
        .unwrap()
        .state_init;
    let boc_encoded: ResultOfEncodeExternalInMessage = client
        .request(
            "boc.encode_external_in_message",
            ParamsOfEncodeExternalInMessage {
                dst: abi_encoded.address.clone(),
                body: abi_parsed["body"].as_str().map(|x| x.to_string()),
                init: Some(init),
                ..Default::default()
            },
        )
        .unwrap();

    assert_eq!(boc_encoded.message, abi_encoded.message);
}

#[test]
fn test_decode_tvc() {
    let client = TestClient::new();
    let tvc_boc = "te6ccgEBBAEALgACCaLwuBzgAgEAJlNvbWUgU21hcnQgQ29udHJhY3QBFP8A9KQT9LzyyAsDAALT";
    let code_boc = "te6ccgEBAgEAEAABFP8A9KQT9LzyyAsBAALT";
    let description = "Some Smart Contract";

    let expected = ResultOfDecodeTvc {
        tvc: Tvc::V1(TvcV1 {
            code: Some(code_boc.to_string()),
            description: Some(description.to_string()),
        }),
    };

    let decoded = decode_tvc(
        client.context().clone(),
        ParamsOfDecodeTvc {
            tvc: tvc_boc.to_string(),
        },
    )
    .unwrap();
    assert_eq!(expected, decoded);

    let expected = json!({
        "tvc": {
            "type": "V1",
            "value": {
                "code": code_boc,
                "description": description
            },
        },
    });

    let decoded = client
        .request::<Value, Value>(
            "boc.decode_tvc",
            json!({
                "tvc": tvc_boc,
            }),
        )
        .unwrap();
    assert_eq!(expected, decoded);
}
